import AuthService from './auth.service';
import ProjectService from './project.service';
import TaskService from './task.service';
import api from './api';

// Initialize the API with current user if available
const initializeAPI = () => {
  const user = AuthService.getCurrentUser();
  if (user && user.accessToken) {
    // Set default headers for all requests
    api.defaults.headers.common['x-access-token'] = user.accessToken;
  }
};

// Call once on import
initializeAPI();

export {
  AuthService,
  ProjectService,
  TaskService,
  api
};
