import axios from 'axios';
import { getCurrentUser, saveUser, removeUser } from '../utils/tokenUtils';
import api from './api';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080/api';

class AuthService {
  async login(username, password) {
    console.log('Attempting login for user:', username);
    const response = await axios.post(`${API_URL}/auth/signin`, { username, password });
    console.log('Login response received:', response.status);

    if (response.data.accessToken) {
      console.log('Access token received, saving user data');
      console.log('Token:', response.data.accessToken.substring(0, 20) + '...');
      saveUser(response.data);
    } else {
      console.log('No access token in response');
    }
    return response.data;
  }

  logout() {
    console.log('Logging out, removing user data');
    removeUser();
  }

  async register(username, email, password, fullName, position, phone) {
    return axios.post(`${API_URL}/auth/signup`, {
      username,
      email,
      password,
      fullName,
      position,
      phone
    });
  }

  getCurrentUser() {
    const user = getCurrentUser();
    console.log('Getting current user:', user ? `${user.username} (ID: ${user.id})` : 'No user found');
    if (user) {
      console.log('User has token:', !!user.accessToken);
    }
    return user;
  }

  getAuthHeader() {
    const user = this.getCurrentUser();
    if (user && user.accessToken) {
      // Ensure the token is properly formatted
      return { 'x-access-token': user.accessToken };
    } else {
      return {};
    }
  }

  // Метод для проверки токена
  async checkToken() {
    try {
      console.log('Checking token validity...');
      const response = await api.get('/debug/check-token');
      console.log('Token check response:', response.data);
      return { valid: true, data: response.data };
    } catch (error) {
      console.error('Token check failed:', error.message);
      return { valid: false, error: error.message };
    }
  }
}

export default new AuthService();
