import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Card, Badge, Button, Row, Col, Table, Form, Modal, Alert } from 'react-bootstrap';
import { toast } from 'react-toastify';
import ProjectService from '../services/project.service';
import TaskService from '../services/task.service';
import AuthService from '../services/auth.service';

function ProjectDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [project, setProject] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showTaskForm, setShowTaskForm] = useState(false);
  const [taskFormData, setTaskFormData] = useState({
    title: '',
    description: '',
    status: 'planned',
    priority: 'medium',
    startDate: '',
    dueDate: '',
    estimatedHours: '',
    assigneeId: ''
  });
  const [users, setUsers] = useState([]);
  const currentUser = AuthService.getCurrentUser();
  const isManagerOrAdmin = currentUser.roles.includes('ROLE_MANAGER') || currentUser.roles.includes('ROLE_ADMIN');

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const projectData = await ProjectService.getProjectById(id);
        setProject(projectData);

        const tasksData = await TaskService.getTasksByProject(id);
        setTasks(tasksData);

        // Здесь должен быть запрос к API для получения пользователей
        // Для демонстрации используем фиктивные данные
        setUsers([
          { id: 1, username: 'worker1', fullName: 'Петр Работников' },
          { id: 2, username: 'worker2', fullName: 'Алексей Строителев' },
          { id: 3, username: 'worker3', fullName: 'Сергей Монтажников' }
        ]);
      } catch (err) {
        setError('Ошибка при загрузке данных проекта');
        toast.error('Ошибка при загрузке данных проекта');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const handleTaskInputChange = (e) => {
    const { name, value } = e.target;
    setTaskFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleTaskSubmit = async (e) => {
    e.preventDefault();
    try {
      await TaskService.createTask({
        ...taskFormData,
        projectId: project.id,
        estimatedHours: Number(taskFormData.estimatedHours)
      });
      
      setShowTaskForm(false);
      setTaskFormData({
        title: '',
        description: '',
        status: 'planned',
        priority: 'medium',
        startDate: '',
        dueDate: '',
        estimatedHours: '',
        assigneeId: ''
      });
      
      // Обновляем список задач
      const tasksData = await TaskService.getTasksByProject(id);
      setTasks(tasksData);
      toast.success('Задача успешно создана!');
    } catch (err) {
      toast.error('Ошибка при создании задачи');
      console.error(err);
    }
  };

  const getStatusBadge = (status) => {
    switch (status) {
      case 'active':
        return <Badge bg="success">Активен</Badge>;
      case 'completed':
        return <Badge bg="secondary">Завершен</Badge>;
      case 'on_hold':
        return <Badge bg="warning">На паузе</Badge>;
      case 'cancelled':
        return <Badge bg="danger">Отменен</Badge>;
      case 'in_progress':
        return <Badge bg="primary">В процессе</Badge>;
      case 'planned':
        return <Badge bg="info">Запланировано</Badge>;
      default:
        return <Badge bg="light">{status}</Badge>;
    }
  };

  const getPriorityBadge = (priority) => {
    switch (priority) {
      case 'high':
        return <Badge bg="danger">Высокий</Badge>;
      case 'medium':
        return <Badge bg="warning">Средний</Badge>;
      case 'low':
        return <Badge bg="success">Низкий</Badge>;
      default:
        return <Badge bg="light">{priority}</Badge>;
    }
  };

  if (loading) {
    return (
      <div className="page-container d-flex justify-content-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error || !project) {
    return (
      <div className="page-container">
        <Alert variant="danger">
          {error || 'Проект не найден'}
        </Alert>
        <Button variant="primary" onClick={() => navigate('/projects')}>
          Вернуться к списку проектов
        </Button>
      </div>
    );
  }

  return (
    <div className="page-container">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>{project.name}</h1>
        <div>
          <Button variant="outline-primary" className="me-2" onClick={() => navigate('/projects')}>
            К списку проектов
          </Button>
          {isManagerOrAdmin && (
            <Button variant="primary" onClick={() => setShowTaskForm(true)}>
              Добавить задачу
            </Button>
          )}
        </div>
      </div>

      <Row className="mb-4">
        <Col md={8}>
          <Card>
            <Card.Body>
              <div className="d-flex justify-content-between mb-3">
                <h5>Описание проекта</h5>
                {getStatusBadge(project.status)}
              </div>
              <p>{project.description}</p>
              
              <Row className="mt-4">
                <Col md={6}>
                  <p><strong>Начало:</strong> {new Date(project.startDate).toLocaleDateString()}</p>
                  <p><strong>Завершение:</strong> {new Date(project.endDate).toLocaleDateString()}</p>
                  <p><strong>Адрес:</strong> {project.location}</p>
                </Col>
                <Col md={6}>
                  <p><strong>Бюджет:</strong> {project.budget.toLocaleString()} руб.</p>
                  <p><strong>Клиент:</strong> {project.clientName}</p>
                  <p><strong>Контакт клиента:</strong> {project.clientContact}</p>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card>
            <Card.Header>
              <h5 className="mb-0">Прогресс проекта</h5>
            </Card.Header>
            <Card.Body>
              {tasks.length > 0 ? (
                <div>
                  <div className="mb-3">
                    <p><strong>Всего задач:</strong> {tasks.length}</p>
                    <p><strong>Завершено:</strong> {tasks.filter(t => t.status === 'completed').length}</p>
                    <p><strong>В процессе:</strong> {tasks.filter(t => t.status === 'in_progress').length}</p>
                    <p><strong>Запланировано:</strong> {tasks.filter(t => t.status === 'planned').length}</p>
                  </div>
                  <div className="progress">
                    <div 
                      className="progress-bar bg-success" 
                      role="progressbar" 
                      style={{ width: `${(tasks.filter(t => t.status === 'completed').length / tasks.length) * 100}%` }} 
                      aria-valuenow={(tasks.filter(t => t.status === 'completed').length / tasks.length) * 100} 
                      aria-valuemin="0" 
                      aria-valuemax="100">
                    </div>
                  </div>
                </div>
              ) : (
                <p>Нет задач для отображения прогресса</p>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <h3 className="mb-3">Задачи проекта</h3>
      {tasks.length === 0 ? (
        <Alert variant="info">
          Задачи не найдены для этого проекта.
          {isManagerOrAdmin && ' Добавьте новую задачу, нажав на кнопку выше.'}
        </Alert>
      ) : (
        <Table responsive striped hover>
          <thead>
            <tr>
              <th>Название</th>
              <th>Статус</th>
              <th>Приоритет</th>
              <th>Сроки</th>
              <th>Часы (план/факт)</th>
              <th>Исполнитель</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map(task => (
              <tr key={task.id}>
                <td>
                  <div>{task.title}</div>
                  <small className="text-muted">{task.description}</small>
                </td>
                <td>{getStatusBadge(task.status)}</td>
                <td>{getPriorityBadge(task.priority)}</td>
                <td>
                  <div>
                    <small>Начало: {new Date(task.startDate).toLocaleDateString()}</small>
                  </div>
                  <div>
                    <small>Конец: {new Date(task.dueDate).toLocaleDateString()}</small>
                  </div>
                </td>
                <td>
                  {task.estimatedHours} / {task.actualHours || 0}
                </td>
                <td>
                  {users.find(u => u.id === task.assigneeId)?.fullName || 'Не назначен'}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {/* Modal для создания задачи */}
      <Modal show={showTaskForm} onHide={() => setShowTaskForm(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Создание новой задачи</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleTaskSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Название задачи *</Form.Label>
              <Form.Control 
                type="text" 
                name="title" 
                value={taskFormData.title} 
                onChange={handleTaskInputChange} 
                required 
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Описание *</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={3} 
                name="description" 
                value={taskFormData.description} 
                onChange={handleTaskInputChange} 
                required 
              />
            </Form.Group>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Статус *</Form.Label>
                  <Form.Select 
                    name="status" 
                    value={taskFormData.status} 
                    onChange={handleTaskInputChange} 
                    required
                  >
                    <option value="planned">Запланировано</option>
                    <option value="in_progress">В процессе</option>
                    <option value="completed">Завершено</option>
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Приоритет *</Form.Label>
                  <Form.Select 
                    name="priority" 
                    value={taskFormData.priority} 
                    onChange={handleTaskInputChange} 
                    required
                  >
                    <option value="low">Низкий</option>
                    <option value="medium">Средний</option>
                    <option value="high">Высокий</option>
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Дата начала *</Form.Label>
                  <Form.Control 
                    type="date" 
                    name="startDate" 
                    value={taskFormData.startDate} 
                    onChange={handleTaskInputChange} 
                    required 
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Срок выполнения *</Form.Label>
                  <Form.Control 
                    type="date" 
                    name="dueDate" 
                    value={taskFormData.dueDate} 
                    onChange={handleTaskInputChange} 
                    required 
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Плановые часы *</Form.Label>
                  <Form.Control 
                    type="number" 
                    name="estimatedHours" 
                    value={taskFormData.estimatedHours} 
                    onChange={handleTaskInputChange} 
                    required 
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Исполнитель *</Form.Label>
                  <Form.Select 
                    name="assigneeId" 
                    value={taskFormData.assigneeId} 
                    onChange={handleTaskInputChange} 
                    required
                  >
                    <option value="">Выберите исполнителя</option>
                    {users.map(user => (
                      <option key={user.id} value={user.id}>
                        {user.fullName} ({user.username})
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            <div className="d-flex justify-content-end mt-3">
              <Button variant="secondary" className="me-2" onClick={() => setShowTaskForm(false)}>
                Отмена
              </Button>
              <Button variant="primary" type="submit">
                Создать задачу
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default ProjectDetails;
