import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Badge, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { ProjectService, TaskService, AuthService } from '../services';

function Dashboard() {
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const currentUser = AuthService.getCurrentUser();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [projectsData, tasksData] = await Promise.all([
          ProjectService.getAllProjects(),
          TaskService.getAllTasks()
        ]);
        
        setProjects(projectsData);
        setTasks(tasksData);
      } catch (err) {
        setError('Ошибка при загрузке данных');
        toast.error('Ошибка при загрузке данных');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const getStatusBadge = (status) => {
    switch (status) {
      case 'completed':
        return <Badge bg="success">Завершено</Badge>;
      case 'in_progress':
        return <Badge bg="primary">В процессе</Badge>;
      case 'planned':
        return <Badge bg="warning">Запланировано</Badge>;
      case 'on_hold':
        return <Badge bg="secondary">На паузе</Badge>;
      default:
        return <Badge bg="info">{status}</Badge>;
    }
  };

  const getPriorityBadge = (priority) => {
    switch (priority) {
      case 'high':
        return <Badge bg="danger">Высокий</Badge>;
      case 'medium':
        return <Badge bg="warning">Средний</Badge>;
      case 'low':
        return <Badge bg="success">Низкий</Badge>;
      default:
        return <Badge bg="info">{priority}</Badge>;
    }
  };

  if (loading) {
    return (
      <div className="page-container d-flex justify-content-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  // Filter tasks assigned to current user
  const myTasks = tasks.filter(task => task.assigneeId === currentUser.id);
  const activeTasks = myTasks.filter(task => task.status !== 'completed');
  const activeProjects = projects.filter(project => project.status === 'active');

  return (
    <div className="page-container">
      <h1>Дашборд</h1>
      <h4 className="mt-4 mb-3">Добро пожаловать, {currentUser.fullName || currentUser.username}!</h4>

      {error && <Alert variant="danger">{error}</Alert>}

      <Row>
        <Col md={6} className="mb-4">
          <Card>
            <Card.Header>
              <h5 className="mb-0">Мои активные задачи</h5>
            </Card.Header>
            <Card.Body>
              {activeTasks.length > 0 ? (
                <div className="list-group">
                  {activeTasks.slice(0, 5).map(task => (
                    <div key={task.id} className={`list-group-item task-list-item ${task.priority}`}>
                      <div className="d-flex w-100 justify-content-between">
                        <h5 className="mb-1">{task.title}</h5>
                        <div>
                          {getStatusBadge(task.status)}
                          {' '}
                          {getPriorityBadge(task.priority)}
                        </div>
                      </div>
                      <p className="mb-1 text-truncate">{task.description}</p>
                      <small className="text-muted">
                        Срок: {new Date(task.dueDate).toLocaleDateString()}
                      </small>
                    </div>
                  ))}
                </div>
              ) : (
                <p>У вас нет активных задач</p>
              )}
              {activeTasks.length > 5 && (
                <div className="text-center mt-3">
                  <Link to="/tasks" className="btn btn-sm btn-outline-primary">
                    Показать все ({activeTasks.length})
                  </Link>
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>

        <Col md={6} className="mb-4">
          <Card>
            <Card.Header>
              <h5 className="mb-0">Активные проекты</h5>
            </Card.Header>
            <Card.Body>
              {activeProjects.length > 0 ? (
                <div className="list-group">
                  {activeProjects.slice(0, 5).map(project => (
                    <Link 
                      key={project.id} 
                      to={`/projects/${project.id}`} 
                      className="list-group-item list-group-item-action">
                      <div className="d-flex w-100 justify-content-between">
                        <h5 className="mb-1">{project.name}</h5>
                        {getStatusBadge(project.status)}
                      </div>
                      <p className="mb-1 text-truncate">{project.description}</p>
                      <small className="text-muted">
                        Бюджет: {project.budget.toLocaleString()} руб.
                      </small>
                    </Link>
                  ))}
                </div>
              ) : (
                <p>Нет активных проектов</p>
              )}
              {activeProjects.length > 5 && (
                <div className="text-center mt-3">
                  <Link to="/projects" className="btn btn-sm btn-outline-primary">
                    Показать все ({activeProjects.length})
                  </Link>
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default Dashboard;
