import axios from 'axios';
import { getToken, removeUser } from '../utils/tokenUtils';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080/api';

const instance = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token to every request
instance.interceptors.request.use(
  (config) => {
    const token = getToken();
    console.log('API Request to:', config.url);
    console.log('Token available:', token ? 'Yes' : 'No');
    
    if (token) {
      config.headers['x-access-token'] = token;
      console.log('Adding token to request headers');
    } else {
      console.log('No token available, request will be unauthorized');
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle errors
instance.interceptors.response.use(
  (response) => {
    return response;
  },
  async (error) => {
    const originalRequest = error.config;
    
    // If error is 401 (Unauthorized) or 403 (Forbidden), handle it
    if ((error.response?.status === 401 || error.response?.status === 403) && !originalRequest._retry) {
      console.log('Authentication error:', error.response?.status, error.response?.data?.message);
      
      // Mark request as retried to prevent infinite loops
      originalRequest._retry = true;
      
      // Check if we have a token at all
      const token = getToken();
      if (!token) {
        console.log('No token available, redirecting to login page');
        removeUser();
        window.location.href = '/login';
        return Promise.reject(error);
      }
      
      try {
        // Check if the error message indicates token expiration
        const isExpiredToken = error.response?.data?.message === 'Неавторизован!' || 
                              error.response?.data?.message === 'jwt expired';
        
        if (isExpiredToken) {
          console.log('Token expired, redirecting to login page');
          removeUser(); // Clear expired token
          window.location.href = '/login?expired=true';
          return Promise.reject(error);
        }
        
        // For other errors, try the request one more time
        console.log('Retrying request once with existing token');
        return instance(originalRequest);
      } catch (refreshError) {
        console.error('Error handling auth:', refreshError);
        removeUser();
        window.location.href = '/login';
        return Promise.reject(error);
      }
    }
    
    return Promise.reject(error);
  }
);

export default instance;
