import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar as BootstrapNavbar, Nav, Container, Button } from 'react-bootstrap';
import { AuthService, api } from '../services';
import { setAuthHeader } from '../utils/tokenUtils';

function Navbar({ user, setUser }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clean up auth state
    AuthService.logout();
    
    // Clear auth header
    setAuthHeader(api, null);
    
    // Update app state
    setUser(null);
    
    // Force a full page reload to ensure a clean state
    window.location.href = '/login';
  };

  // Check if user has a specific role
  const hasRole = (roleName) => {
    return user && user.roles && (
      user.roles.includes(roleName) || 
      user.roles.includes(`ROLE_${roleName.toUpperCase()}`)
    );
  };

  const isAdmin = hasRole('admin');
  const isManager = hasRole('manager');
  const isWorker = !isAdmin && !isManager;

  return (
    <BootstrapNavbar bg="primary" variant="dark" expand="lg" className="mb-4">
      <Container style={{ maxWidth: '1320px' }}>
        <BootstrapNavbar.Brand as={Link} to="/">Управление проектами</BootstrapNavbar.Brand>
        <BootstrapNavbar.Toggle aria-controls="basic-navbar-nav" />
        <BootstrapNavbar.Collapse id="basic-navbar-nav">
          {user ? (
            <>
              <Nav className="me-auto">
                <Nav.Link as={Link} to="/dashboard">Дашборд</Nav.Link>
                <Nav.Link as={Link} to="/projects">Проекты</Nav.Link>
                {isWorker && <Nav.Link as={Link} to="/tasks">Задачи</Nav.Link>}
                {isAdmin && <Nav.Link as={Link} to="/admin">Панель администратора</Nav.Link>}
              </Nav>
              <Nav>
                <Nav.Link as={Link} to="/profile">Профиль</Nav.Link>
                <Button variant="outline-light" onClick={handleLogout}>Выйти</Button>
              </Nav>
            </>
          ) : (
            <Nav className="ms-auto">
              <Nav.Link as={Link} to="/login">Войти</Nav.Link>
              <Nav.Link as={Link} to="/register">Регистрация</Nav.Link>
            </Nav>
          )}
        </BootstrapNavbar.Collapse>
      </Container>
    </BootstrapNavbar>
  );
}

export default Navbar;
