import React, { useState, useEffect } from 'react';
import { Table, Badge, Button, Form, Row, Col, Card, Alert } from 'react-bootstrap';
import { toast } from 'react-toastify';
import { TaskService, AuthService } from '../services';

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [filteredTasks, setFilteredTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filters, setFilters] = useState({
    status: 'all',
    priority: 'all',
    search: ''
  });
  const currentUser = AuthService.getCurrentUser();

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        setLoading(true);
        const data = await TaskService.getAllTasks();
        setTasks(data);
        setFilteredTasks(data);
      } catch (err) {
        setError('Ошибка при загрузке задач');
        toast.error('Ошибка при загрузке задач');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [filters, tasks]);

  const applyFilters = () => {
    let result = [...tasks];
    
    // Фильтр по статусу
    if (filters.status !== 'all') {
      result = result.filter(task => task.status === filters.status);
    }
    
    // Фильтр по приоритету
    if (filters.priority !== 'all') {
      result = result.filter(task => task.priority === filters.priority);
    }
    
    // Фильтр по поиску (в названии или описании)
    if (filters.search.trim()) {
      const searchLower = filters.search.toLowerCase();
      result = result.filter(
        task => 
          task.title.toLowerCase().includes(searchLower) || 
          task.description.toLowerCase().includes(searchLower)
      );
    }
    
    setFilteredTasks(result);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleUpdateStatus = async (taskId, newStatus) => {
    try {
      await TaskService.updateTask(taskId, { status: newStatus });
      
      // Обновляем состояние задачи локально
      setTasks(prevTasks => 
        prevTasks.map(task => 
          task.id === taskId ? { ...task, status: newStatus } : task
        )
      );
      
      toast.success('Статус задачи обновлен');
    } catch (err) {
      toast.error('Ошибка при обновлении статуса задачи');
      console.error(err);
    }
  };

  const getStatusBadge = (status) => {
    switch (status) {
      case 'completed':
        return <Badge bg="success">Завершено</Badge>;
      case 'in_progress':
        return <Badge bg="primary">В процессе</Badge>;
      case 'planned':
        return <Badge bg="warning">Запланировано</Badge>;
      case 'on_hold':
        return <Badge bg="secondary">На паузе</Badge>;
      default:
        return <Badge bg="info">{status}</Badge>;
    }
  };

  const getPriorityBadge = (priority) => {
    switch (priority) {
      case 'high':
        return <Badge bg="danger">Высокий</Badge>;
      case 'medium':
        return <Badge bg="warning">Средний</Badge>;
      case 'low':
        return <Badge bg="success">Низкий</Badge>;
      default:
        return <Badge bg="info">{priority}</Badge>;
    }
  };

  if (loading) {
    return (
      <div className="page-container d-flex justify-content-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  // Получаем задачи, назначенные текущему пользователю
  const myTasks = filteredTasks.filter(task => task.assigneeId === currentUser.id);

  return (
    <div className="page-container">
      <h1 className="mb-4">Мои задачи</h1>
      
      {error && <Alert variant="danger">{error}</Alert>}
      
      <Card className="mb-4">
        <Card.Body>
          <h5>Фильтры</h5>
          <Row>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Статус</Form.Label>
                <Form.Select 
                  name="status" 
                  value={filters.status} 
                  onChange={handleFilterChange}
                >
                  <option value="all">Все статусы</option>
                  <option value="planned">Запланировано</option>
                  <option value="in_progress">В процессе</option>
                  <option value="completed">Завершено</option>
                  <option value="on_hold">На паузе</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Приоритет</Form.Label>
                <Form.Select 
                  name="priority" 
                  value={filters.priority} 
                  onChange={handleFilterChange}
                >
                  <option value="all">Все приоритеты</option>
                  <option value="low">Низкий</option>
                  <option value="medium">Средний</option>
                  <option value="high">Высокий</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Поиск</Form.Label>
                <Form.Control 
                  type="text" 
                  name="search" 
                  value={filters.search} 
                  onChange={handleFilterChange}
                  placeholder="Поиск по названию или описанию" 
                />
              </Form.Group>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      {myTasks.length === 0 ? (
        <Alert variant="info">
          Задачи не найдены. Проверьте настройки фильтров или обратитесь к менеджеру проекта для назначения задач.
        </Alert>
      ) : (
        <Table responsive striped hover>
          <thead>
            <tr>
              <th>Название</th>
              <th>Проект</th>
              <th>Статус</th>
              <th>Приоритет</th>
              <th>Сроки</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {myTasks.map(task => (
              <tr key={task.id} className={`task-list-item ${task.priority}`}>
                <td>
                  <div className="fw-bold">{task.title}</div>
                  <small className="text-muted">{task.description}</small>
                </td>
                <td>
                  {task.project?.name || 'Н/Д'}
                </td>
                <td>{getStatusBadge(task.status)}</td>
                <td>{getPriorityBadge(task.priority)}</td>
                <td>
                  <div>
                    <small>Начало: {new Date(task.startDate).toLocaleDateString()}</small>
                  </div>
                  <div>
                    <small>Срок: {new Date(task.dueDate).toLocaleDateString()}</small>
                  </div>
                </td>
                <td>
                  <div className="d-flex">
                    {task.status === 'planned' && (
                      <Button
                        variant="outline-primary"
                        size="sm"
                        onClick={() => handleUpdateStatus(task.id, 'in_progress')}
                        className="me-2"
                      >
                        Начать
                      </Button>
                    )}
                    
                    {task.status === 'in_progress' && (
                      <Button
                        variant="outline-success"
                        size="sm"
                        onClick={() => handleUpdateStatus(task.id, 'completed')}
                      >
                        Завершить
                      </Button>
                    )}
                    
                    {task.status === 'completed' && (
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => handleUpdateStatus(task.id, 'in_progress')}
                      >
                        Возобновить
                      </Button>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
}

export default Tasks;
